package com.javayadu.ThirtyDaysofCode;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =10;
		if(i==10){
			System.out.println("i=" + i + " from first if");
			i--;
		}
		if(i==9){
			System.out.println("i=" + i + " from second if");
		}

	}

}
